import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [rsvpStatus, setRsvpStatus] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    axios.get('http://127.0.0.1:8000/api/v1/events/', {
      headers: {
        Authorization: `Token ${token}`,
      },
    })
    .then(res => {
      setEvents(res.data);
      setLoading(false);
    })
    .catch(err => {
      console.error(err);
      setLoading(false);
    });
  }, [navigate]);

  const handleRSVP = (eventId) => {
    const token = localStorage.getItem('token');
    axios.post('http://127.0.0.1:8000/api/v1/attendance/', { event: eventId }, {
      headers: {
        Authorization: `Token ${token}`,
      },
    })
    .then(res => setRsvpStatus(`Successfully RSVP'd for event ID ${eventId}`))
    .catch(err => setRsvpStatus(`Error: ${err.response?.data?.detail || 'Something went wrong'}`));
  };

  if (loading) return <div className="text-center mt-5">Loading events...</div>;

  return (
    <>
      <Navbar />
      <div className="container py-5">
        <h2 className="text-primary mb-4">Upcoming Events</h2>
        {rsvpStatus && <div className="alert alert-info">{rsvpStatus}</div>}
        <div className="row">
          {events.map((event) => (
            <div className="col-md-6 col-lg-4 mb-4" key={event.id}>
              <div className="card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title text-primary">{event.title}</h5>
                  <p className="card-text">{event.description}</p>
                  <p className="mb-1"><strong>Location:</strong> {event.location}</p>
                  <p className="mb-1"><strong>Start:</strong> {new Date(event.start_date).toLocaleString()}</p>
                  <p className="mb-3"><strong>End:</strong> {new Date(event.end_date).toLocaleString()}</p>
                  <button className="btn btn-outline-primary mt-auto" onClick={() => handleRSVP(event.id)}>
                    RSVP
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Events;
