import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar';

const MyRSVPs = () => {
  const [rsvps, setRsvps] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    axios.get('http://127.0.0.1:8000/api/v1/attendance/', {
      headers: {
        Authorization: `Token ${token}`,
      },
    })
    .then(res => {
      setRsvps(res.data);
      setLoading(false);
    })
    .catch(err => {
      console.error(err);
      setLoading(false);
    });
  }, [navigate]);

  if (loading) return <div className="text-center mt-5">Loading your RSVPs...</div>;

  return (
    <>
      <Navbar />
      <div className="container py-5">
        <h2 className="mb-4 text-primary">My RSVPs</h2>
        {rsvps.length === 0 ? (
          <div className="alert alert-info">You haven’t RSVP’d to any events yet.</div>
        ) : (
          <div className="table-responsive">
            <table className="table table-bordered table-hover">
              <thead className="table-light">
                <tr>
                  <th>Event Title</th>
                  <th>Description</th>
                  <th>Start Date</th>
                  <th>End Date</th>
                  <th>Location</th>
                </tr>
              </thead>
              <tbody>
                {rsvps.map((rsvp, index) => (
                  <tr key={index}>
                    <td>{rsvp.event.title}</td>
                    <td>{rsvp.event.description}</td>
                    <td>{new Date(rsvp.event.start_date).toLocaleString()}</td>
                    <td>{new Date(rsvp.event.end_date).toLocaleString()}</td>
                    <td>{rsvp.event.location}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </>
  );
};

export default MyRSVPs;
