import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';

const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get('http://127.0.0.1:8000/api/v1/events/', {
      headers: { Authorization: `Token ${token}` },
    })
    .then(res => setEvents(res.data))
    .catch(err => console.error(err));
  }, []);

  return (
    <>
      <Navbar />
      <div className="container py-5">
        <h2 className="mb-4 text-primary">All Events</h2>
        {events.length === 0 ? (
          <div className="alert alert-warning">No events available.</div>
        ) : (
          <div className="row row-cols-1 row-cols-md-2 g-4">
            {events.map(event => (
              <div key={event.id} className="col">
                <div className="card h-100 shadow-sm">
                  <div className="card-body">
                    <h5 className="card-title">
                      {event.title}
                      {event.has_rsvped && (
                        <span
                          className="badge bg-success ms-2"
                          title="You have RSVP’d to this event"
                        >
                          ✓ RSVP’d
                        </span>
                      )}
                    </h5>
                    <p className="card-text">{event.description}</p>
                    <ul className="list-unstyled">
                      <li><strong>Start:</strong> {new Date(event.start_date).toLocaleString()}</li>
                      <li><strong>End:</strong> {new Date(event.end_date).toLocaleString()}</li>
                      <li><strong>Location:</strong> {event.location}</li>
                    </ul>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default Events;
