import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';


const Dashboard = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await axios.get('/api/v1/events/', {
          headers: { Authorization: `Token ${token}` },
        });
        setEvents(res.data);
      } catch (err) {
        setError('Failed to load events. Please ensure you are logged in.');
      }
    };

    fetchEvents();
  }, [token]);

  return (
  <>
    <Navbar />
    <div className="container py-4">
      <h2 className="mb-4 text-center" style={{ color: '#4B0082' }}>Welcome to Your Dashboard</h2>
      {error && <p className="text-danger text-center">{error}</p>}
      <div className="row">
        {events.map((event) => (
          <div className="col-md-6 col-lg-4 mb-4" key={event.id}>
            <div className="card shadow h-100 border-0">
              <div className="card-body">
                <h5 className="card-title" style={{ color: '#4B0082' }}>{event.title}</h5>
                <p className="card-text text-muted">{event.description}</p>
                <span className="badge bg-secondary">
                  {event.start_time} â†’ {event.end_time}
                </span>
              </div>
              <div className="card-footer bg-white text-end border-0">
                <button className="btn btn-sm text-white" style={{ backgroundColor: '#FF6F61' }}>
                  View Details
                </button>
              </div>
            </div>
          </div>
        ))}
        {events.length === 0 && !error && (
          <p className="text-center text-muted">No events available.</p>
        )}
      </div>
    </div>
  </>
);

};

export default Dashboard;
