import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [rsvps, setRsvps] = useState([]);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProfileAndRSVPs = async () => {
      try {
        const headers = { Authorization: `Token ${token}` };

        const [profileRes, rsvpRes] = await Promise.all([
          axios.get('/api/v1/profile/', { headers }),
          axios.get('/api/v1/attendance/', { headers })
        ]);

        setProfile(profileRes.data);
        setRsvps(rsvpRes.data);
      } catch {
        setError('Failed to load profile or RSVP history.');
      }
    };

    fetchProfileAndRSVPs();
  }, [token]);

  return (
    <>
      <Navbar />
      <div className="container py-4">
        <h2 className="text-center mb-4" style={{ color: '#4B0082' }}>My Profile</h2>

        {error && <p className="text-danger text-center">{error}</p>}

        {profile && (
          <div className="card shadow-sm mx-auto mb-5" style={{ maxWidth: '400px' }}>
            <div className="card-body">
              <h5 className="card-title" style={{ color: '#4B0082' }}>Hello, {profile.username}</h5>
              <p className="card-text"><strong>Email:</strong> {profile.email}</p>
              <p className="card-text"><strong>User ID:</strong> {profile.id}</p>
            </div>
          </div>
        )}

        <h4 className="text-center mb-3" style={{ color: '#4B0082' }}>My RSVPs</h4>
        <div className="row">
          {rsvps.map((entry) => (
            <div className="col-md-6 col-lg-4 mb-3" key={entry.id}>
              <div className="card h-100 border-0 shadow-sm">
                <div className="card-body">
                  <h5 className="card-title" style={{ color: '#4B0082' }}>
                    {entry.event?.title}
                  </h5>
                  <p className="card-text text-muted">{entry.event?.description}</p>
                  <span className="badge bg-secondary">
                    {entry.event?.start_time} â†’ {entry.event?.end_time}
                  </span>
                </div>
              </div>
            </div>
          ))}
          {rsvps.length === 0 && (
            <p className="text-center text-muted">No RSVP history yet.</p>
          )}
        </div>
      </div>
    </>
  );
};

export default Profile;
