import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [rsvps, setRsvps] = useState([]);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [ordering, setOrdering] = useState('event__start_time');
  const [page, setPage] = useState(1);
  const [count, setCount] = useState(0);
  const token = localStorage.getItem('token');

  const fetchProfile = async () => {
    try {
      const res = await axios.get('/api/v1/profile/', {
        headers: { Authorization: `Token ${token}` },
      });
      setProfile(res.data);
    } catch {
      setError('Failed to load profile.');
    }
  };

  const fetchRSVPs = async () => {
    try {
      const res = await axios.get(`/api/v1/attendance/`, {
        headers: { Authorization: `Token ${token}` },
        params: { search, ordering, page }
      });
      setRsvps(res.data.results);
      setCount(res.data.count);
    } catch {
      setError('Failed to load RSVP history.');
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  useEffect(() => {
    fetchRSVPs();
  }, [search, ordering, page]);

  const totalPages = Math.ceil(count / 10);

  return (
    <>
      
      <div className="container py-4">
        <h2 className="text-center mb-4" style={{ color: '#4B0082' }}>My Profile</h2>

        {error && <p className="text-danger text-center">{error}</p>}

        {profile && (
          <div className="card shadow-sm mx-auto mb-5" style={{ maxWidth: '400px' }}>
            <div className="card-body">
              <h5 className="card-title" style={{ color: '#4B0082' }}>Hello, {profile.username}</h5>
              <p className="card-text"><strong>Email:</strong> {profile.email}</p>
              <p className="card-text"><strong>User ID:</strong> {profile.id}</p>
            </div>
          </div>
        )}

        <div className="d-flex justify-content-between align-items-center mb-3">
          <input
            type="text"
            className="form-control w-50"
            placeholder="Search RSVPs..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ borderColor: '#4B0082' }}
          />
          <select
            className="form-select w-25 ms-3"
            value={ordering}
            onChange={(e) => setOrdering(e.target.value)}
          >
            <option value="event__start_time">Sort: Start Time ↑</option>
            <option value="-event__start_time">Sort: Start Time ↓</option>
            <option value="event__title">Sort: Title A-Z</option>
            <option value="-event__title">Sort: Title Z-A</option>
          </select>
        </div>

        <div className="row">
          {rsvps.map((entry) => (
            <div className="col-md-6 col-lg-4 mb-3" key={entry.id}>
              <div className="card h-100 border-0 shadow-sm">
                <div className="card-body">
                  <h5 className="card-title" style={{ color: '#4B0082' }}>
                    {entry.event?.title}
                  </h5>
                  <p className="card-text text-muted">{entry.event?.description}</p>
                  <span className="badge bg-secondary">
                    {entry.event?.start_time} → {entry.event?.end_time}
                  </span>
                </div>
              </div>
            </div>
          ))}
          {!rsvps.length && (
            <p className="text-center text-muted">No RSVP history yet.</p>
          )}
        </div>

        {totalPages > 1 && (
          <div className="d-flex justify-content-center mt-4">
            <nav>
              <ul className="pagination">
                {[...Array(totalPages)].map((_, i) => (
                  <li key={i} className={`page-item ${page === i + 1 ? 'active' : ''}`}>
                    <button className="page-link" onClick={() => setPage(i + 1)}>
                      {i + 1}
                    </button>
                  </li>
                ))}
              </ul>
            </nav>
          </div>
        )}
      </div>
    </>
  );
};

export default Profile;
