import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';

const Dashboard = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const [rsvpStatus, setRsvpStatus] = useState({}); // track RSVP per event
  const [toast, setToast] = useState({ visible: false, message: '', variant: '' });
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await axios.get('/api/v1/events/', {
          headers: { Authorization: `Token ${token}` },
        });
        setEvents(res.data);
      } catch {
        setError('Failed to load events. Please ensure you are logged in.');
      }
    };
    fetchEvents();
  }, [token]);

  const handleRSVP = async (eventId) => {
    try {
      await axios.post(
        '/api/v1/attendance/',
        { event: eventId },
        { headers: { Authorization: `Token ${token}` } }
      );
      setRsvpStatus((prev) => ({ ...prev, [eventId]: true }));
      setToast({ visible: true, message: 'ðŸŽ‰ RSVP successful!', variant: 'success' });
    } catch {
      setToast({ visible: true, message: 'âš ï¸ RSVP failed. Try again later.', variant: 'danger' });
    }
  };

  const closeToast = () => {
    setToast({ visible: false, message: '', variant: '' });
  };

  return (
    <>
      <Navbar />

      {/* Toast Notification */}
      <div
        className={`toast align-items-center text-white bg-${toast.variant} border-0 position-fixed`}
        role="alert"
        style={{ top: '1rem', right: '1rem', display: toast.visible ? 'flex' : 'none' }}
      >
        <div className="d-flex">
          <div className="toast-body">{toast.message}</div>
          <button
            type="button"
            className="btn-close btn-close-white me-2 m-auto"
            aria-label="Close"
            onClick={closeToast}
          />
        </div>
      </div>

      <div className="container py-4">
        <h2 className="mb-4 text-center" style={{ color: '#4B0082' }}>
          Welcome to Your Dashboard
        </h2>
        {error && <p className="text-danger text-center">{error}</p>}

        <div className="row">
          {events.map((event) => (
            <div className="col-md-6 col-lg-4 mb-4" key={event.id}>
              <div className="card shadow h-100 border-0">
                <div className="card-body">
                  <h5 className="card-title" style={{ color: '#4B0082' }}>
                    {event.title}
                  </h5>
                  <p className="card-text text-muted">{event.description}</p>
                  <span className="badge bg-secondary mb-2 d-block">
                    {event.start_time} â†’ {event.end_time}
                  </span>
                  <button
                    className="btn btn-sm text-white"
                    style={{ backgroundColor: '#FF6F61' }}
                    onClick={() => handleRSVP(event.id)}
                    disabled={!!rsvpStatus[event.id]}
                  >
                    {rsvpStatus[event.id] ? 'RSVPed' : 'RSVP'}
                  </button>
                </div>
              </div>
            </div>
          ))}
          {!events.length && !error && (
            <p className="text-center text-muted">No events available.</p>
          )}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
